package com.example.ukrainetouristic.category;

import android.os.Bundle;
import android.view.Window;
import android.view.WindowManager;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.ukrainetouristic.R;

import java.util.ArrayList;
import java.util.List;

public class PrePlaceInfoActivityPalacu extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.recycler_view_item);

        Window w = getWindow();
        w.setFlags(WindowManager.LayoutParams.FLAG_LAYOUT_NO_LIMITS, WindowManager.LayoutParams.FLAG_LAYOUT_NO_LIMITS);

        RecyclerView recyclerView = findViewById(R.id.rv_list);
        List<ItemCategory> mlist = new ArrayList<>();
        mlist.add(new ItemCategory(21,R.drawable.cat2_vishnivez1, "Палац Вишневецьких", "смт Вишнівець, Тернопільська обл. 18ст.","Палац розташований на високому пагорбі, з висоти якого відкривається неймовірний краєвид на річку Горинь та більшу частину містечка Вишнівець. Вишневецький замок зазнавав неодноразових руйнувань, збереглися лише кілька фортифікаційних будівель з 17ст. В нинішньому вигляді палац було збудовано у 1730-х роках Міхалом Сервацієм Вишневецьким, який пізніше зазнав ще кількох реконструкцій. У 18ст. палац перейшов до представників роду Мнішеків, які надали палацу королівського блиску та вишуканості. Величезні колекції живопису (одних портретів з приватних зібрань Вишнівецьких, Потоцьких, Сангушко, Чарторийських, Острозьких… близько шестиста), скульптури, антикварні меблі, голландські кахлі, література (близько двадцяти однієї тисячі томів), зброя, посуд. Оноре де Бальзак, який відвідав Вишнівець у 1848 році, назвав палац і парк «малим Версалем». Навесні 1944-го під час бойових дій зазнав значних руйнувань. У 1950-х Вишнівецький палац відремонтовано, однак первісний вигляд, зокрема його внутрішнє убранство, не відновлено. Зараз палац відкритий для відвідувачів, має велику колецію експонатів, частина палацу на реконструкції"));
        mlist.add(new ItemCategory(22,R.drawable.cat2_sangushko_izaslav, "Палац Сангушків", "Ізяслав, Хмельницька обл. 1754-1770р.","Палац - барроковий комплекс, збудовано для барбари Сангушко, як урядовий центр князівського роду у волинських володіннях. Палац має унікальне планування'piano nobile', якого не мають інші палаци тогочасної Речі Посполитої. В кінці 19ст. палац потрапив до власності Російської імператорської армії, яка не ставилась до будівлі з належною історичною значимістю будівлі та перетворили палац на казарму. В радянські часи палац продовжив занепадати та руйнуватись. Після проголошення Україною незалежності ставлення до пам'ятки не змінилося: без належної підтримки палац руйнується і загрожує зовсім зникнути з лиця землі."));
        mlist.add(new ItemCategory(23,R.drawable.cat2_badeni, "Палац Бадені", "Коропець, Тернопільська обл. 1893-1906р.","Дістатись сюди - це випробування для людей не слабких духом та терпінням, через суцільне бездоріжжя, але дорогою Вам відкриються гарні краєвиди та панорами. В результаті винагородою буде один з кращих палаців західної України з непересічною історією його власника. \n Граф Казімєж (Казимир) Бадені (1846–1909) був намісником Галичини, прем'єром Австрії та міністром внутрішніх справ. Прославився «баденівськими виборами» до парламенту 1897 року, коли був запроваджений антиукраїнський терор. Палац у Коропці збудований у класично-синкретичному стилі з розкішними бароковими ліпними геральдичними атрибутами, серед яких між рушниць і мисливських ріжків почесне місце займає барельєф з зображенням перехрещених коси і ціпа. У Першу світову війну в 1914—1920 роках палац було сильно пошкоджено та пограбовано.\n" +
                "\n" +
                "Останнім власником Коропця був наймолодший син Станіслава Бадені — Стефан Бадені (пол. Stefan Badeni) (1885—1961). Після захоплення Коропця комуністами в 1939 році, він був змушений мігрувати з родиною до Будапешту. В подальшому був ув'язнений нацистами у концтабір в Маутхаузені, де вижив. Останні роки доживав у Дубліні."));
        mlist.add(new ItemCategory(24,R.drawable.cat2_rymianzev1, "Палац Рум'янцева-Задунайського", "Вишеньки, Чернігівська обл. 1782-1787р.","Власником палацу був граф, державний діяч Російської імперії (Московії), полководець, намісник лівобережної України та коханець Катерини другої. Користувався величезною прихильністю імператриці, яка наділила його правом володіння величерною кількістю земель, більшою мірою в Україні. У Вишеньках і Черешеньках протягом 1782–1787 рр. на березі Десни він побудував цілий комплекс будівель в модних для того часу формах неокласичного романтизму кінця 18 століття. Палац був побудований спеціально до приїзду Катерини II під час її подорожі до Криму. До наших днів збереглася церква і лише половина палацу. У 1809 році син Рум'янцева продав палац поміщику Судіенко, проте пізніше передумав. Щоб розірвати договір, він не придумав нічого кращого, як розібрати половину палацу. Те, що залишилося, новий господар, князь Долгоруков, відремонтував в 1840 році. У радянські часи на території садиби влаштували піонерський табір. Зараз тут розміщується дитячий оздоровчий табір «Сузір'я». "));
        mlist.add(new ItemCategory(25,R.drawable.cat2_kachanivka1, "Качанівський палац", "Качанівка, Чернігівська обл. 1770-ті.","Національний історико-культурний заповідник 'Качанівка'. Комплекс споруд в стилі класицизму з елементами, властивими періоду історизму, цегляна, другий поверх дерев'яний, фанерований цеглою. У інтер'єрах використано червоне дерево, дуб, мармур, штучний мармур, поліхромні метлахські кахлі, кольорове скло. Качанівський парк має площу 560га і є одним з найбільших парків Європи. Загальнадовжина доріжок в парку 60км. "));
        mlist.add(new ItemCategory(26,R.drawable.cat2_galaganiv1, "Палац Галаганів", "Сокиренці, Чернігівська обл. 1823-1829р.","Інша назва палацу - Сокиринський архітектурно-парковий комплекс. В 1829 році поміщик Павло Галаган спорудив великий мармуровий палац на 60 кімнат в стилі ампір, чимось він нагадує будівлю Верховної Ради, оскільки також має високий купол посередині. Поряд з палацом - парк на 40га. Палац має велику музейну експозицію, яку неодмінно варто відвідати."));
        mlist.add(new ItemCategory(27,R.drawable.cat2_bilokrunucki1, "Білокриницький палац", "с. Білокриниця, Тернопільська обл. сер.19ст.","Недалечко від міста Кременець, знаходиться цікаве село Білокриниця. А цікаве воно тим, що тут збудований чудовий замок-палац XIX століття у англо-готичному стилі, з елемантами ренесансу. Історія цієї унікальної споруди дуже давня, і тягнеться ще з ХVІІ. Білокриницький замок у різні часи перебував у різних власників. " +
                "Бачили ці стіни розквіт та занепад, а зараз тут знаходиться Лісотехнічне училище. Хочу зауважити, що палац та його територія знаходиться в досить хорошому стані."));
        mlist.add(new ItemCategory(28,R.drawable.cat2_betrginski_tereschenki1, "Пала́ц Бержи́нських-Тере́щенків", "Андрушівка, Житомирська обл. сер. 19ст.","Палац розташований у центрі міста, оточений старечим парком, неподалік від будівлі - став. На сьогодні в палаці збереглись автентичні мармурові сходи та ліплені позолочені стелі в деяких приміщеннях. Першим власником палацу був поляк за походженням, камергер російського двору Станіслав Бержинський. За його наказом палац звели у середині XIX століття. 1869 року цукровий завод — перший на Житомирщині, збудований 1843 року, і графський маєток у синів Станіслава Бержинскього придбав Артемій Терещенко, що започаткував династію підприємців-цукрозаводчиків і меценатів. Зараз тут діюча школа."));
        mlist.add(new ItemCategory(29,R.drawable.cat2_rozumovski, "Палац Розумовського", "Батурин, Чернігівська обл. 1799-1803р.","Палац останнього гетьмана Війська Запорізького, графа, генерал-фельдмаршала - Кирила Розумовського. Саме за його керування за наказу Катерини другої було ліквідовано Гетьманат. За що йому було даровано нові титули та землі. Варто сказати, що останній гетьман намагався провести реформи, які мали б призвести до розвитку Гетьманщини, але в 1764 році Гетьманат було ліквідовано за наказом Катерини другої. З 1794 р. Кирило Розумовський постійно проживає в Батурині. Саме тоді він і замислює будівництво грандіозного палацово-паркового ансамблю.\n" +
                "\n" +
                "Палац гетьмана Кирила Розумовського — видатна пам’ятка доби класицизму. Нині палац реставновано та в ньому відбуваються масштабні культурні події. Палац має велику кількість експонатів. Рекомендовано до відвідування."));
        mlist.add(new ItemCategory(210,R.drawable.cat2_chervone_tereschenki, "Палац Терещенків", "смт. Червоне, Житомирська обл. 19ст.","Від Городківки прямуємо понад 10 км бруківкою до смт. Червоне, де зберігся найгарніший на теренах Житомирщини палац, який належав цукрозаводчикам Терещенкам. Нині, доречі, цукровий завод працює. На сьогоднішній день палац належить Жіночому монастирю с. Червоне. Монахині стараються дивись за порядком навколо замку+ є декілька відремонтованих кімнат у замку, де вони і проживають. \n" +
                "Колись палац  був оточений великим парком, який межував із садами. Їхня загальна площа становила 58 га. У самому палаці був театр, тераса, куди Терещенки запрошували поважних гостей. Збережені дві копії фонтанів з Барселони. За маєтком розкидається парк з ставком. ВАРТО ЗАУВАЖИТИ, що у с. Червоне, 1909 року Терещенки збудували один із перших авіазаводів, випускали там 7 моделей літаків, по 2-а в місяць. У власності Терещенків палац знаходився до Жовтневого перевороту 1917 року. Після перевороту там знаходився притулок, потім технічне ПТУ.. Стан споруди можна оцінити на фото- все помаленьку розвалюється, туристичний потенціал великий.  Відвідати рекомендую."));
        Adapter_recycler adapter = new Adapter_recycler(this, mlist);
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
    }
}
